mapper.select:
SELECT E.EMPLOYEE_ID, E.FIRST_NAME, E.LAST_NAME, E.EMAIL, E.PHONE_NUMBER, E.HIRE_DATE, E.JOB_ID, J.JOB_TITLE, E.DEPARTMENT_ID, D.DEPARTMENT_NAME FROM EMPLOYEES E, DEPARTMENTS D, JOBS J WHERE E.DEPARTMENT_ID=D.DEPARTMENT_ID AND E.JOB_ID=J.JOB_ID
DEPARTMENT_ID = #{department_id}
AND JOB_ID = #{job_id}
AND upper(FIRST_NAME) LIKE '%'||upper(#{first_name})||'%'
ORDER BY 1

mapper.update:
UPDATE EMPLOYEES
FIRST_NAME=#{first_name}, LAST_NAME=#{last_name}, EMAIL=#{email}, PHONE_NUMBER=#{phone_number}, HIRE_DATE=#{hire_date}, JOB_ID=#{job_id}, MANAGER_ID=#{manager_id}, DEPARTMENT_ID=#{department_id}
WHERE EMPLOYEE_ID=#{employee_id}
